import axios from 'axios';

const url = 'https://localhost:5000/posts'; //simply returns all the posts that we curently have in backend router where all the posts resides in the dtabase.

export const fetchPosts = () => axios.get(url);
export const createPost = (newPost) => axios.post(url, newPost);
